import React, {Component} from 'react';
import Navigation from './components/Navigation/Navigation';
import './App.css';
import Clarifai from 'clarifai';
import Logo from './components/Logo/Logo'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import Rank from './components/Rank/Rank'
import FaceRecognition from './components/FaceRecognition/FaceRecognition'
import Particles from 'react-particles-js';

const app = new Clarifai.App({
 apiKey: '59f85ba38c6d4590a8389effb20473f0'
});

class App extends Component{

  constructor(){

    super();
    this.state={

      input:'',

      imageUrl:'',
      box: {}
    }

  }

  calculateFaceLocation=(data)=>{

    const clarifaiFace=data.rawData.outputs[0].data.regions[0].region_info.bounding_box;
    const image=document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    console.log(width, height);



  }

  onInputChange = (event)=>{

    this.setState({input:event.target.value});
  }
  onSubmit = ()=>{

    this.setState({imageUrl:this.state.input});
    console.log(this.state.imageUrl);
    app.models.predict(



        //"53e1df302c079b3db8a0a36033ed2d15",
        //"5e026c5fae004ed4a83263ebaabec49e",
        Clarifai.FACE_DETECT_MODEL,
        this.state.input
      ).then(

        function(response){

          this.calculateFaceLocation(response);

          // console.log(response.rawData.outputs[0].data.regions[0].region_info.bounding_box);
        },
        function(err) {
          // body...
        }

      );
  }


  render(){
    return (
    <div className="App">

    <Particles className='Particles' 
              params={{
                particles: {
                  number: {
                    value:100,
                    density: {
                      enable: true,
                      value_area:800
                    }
                  }
                }
              }}

            />

      <Navigation />
      <Logo />
      <Rank/>
      <ImageLinkForm onInputChange={this.onInputChange} onSubmit={this.onSubmit}/>
      <FaceRecognition imageUrl={this.state.imageUrl}/>


    </div>
    );
  }

}

export default App;
